DROP TABLE products;
CREATE TABLE IF NOT EXISTS `products` (
    `product_id` INT,
    `name` VARCHAR(59) CHARACTER SET utf8,
    `old_price` INT,
    `new_price` INT,
    `display` VARCHAR(23) CHARACTER SET utf8,
    `processor` VARCHAR(24) CHARACTER SET utf8,
    `card` VARCHAR(18) CHARACTER SET utf8,
    `battery` VARCHAR(21) CHARACTER SET utf8,
    `weight` FLOAT,
    `discount_percent` INT,
    `ram` INT,
    `storage` VARCHAR(8) CHARACTER SET utf8,
    `banner_img` VARCHAR(2048) CHARACTER SET utf8
);
