CREATE DATABASE IF NOT EXISTS `review`;
USE `review`;

CREATE TABLE IF NOT EXISTS `products` (
    `product_id` INT PRIMARY KEY AUTO_INCREMENT,
    `name` VARCHAR(255) CHARACTER SET utf8,
    `old_price` FLOAT,
    `current_price` FLOAT,
    `discount_percent` INT,
    `main_img` VARCHAR(255) CHARACTER SET utf8,
    `short_processor` VARCHAR(255) CHARACTER SET utf8,
    `short_display` VARCHAR(255) CHARACTER SET utf8,
    `short_card` VARCHAR(255) CHARACTER SET utf8,
    `short_weight` VARCHAR(255) CHARACTER SET utf8,
    
    `processor` VARCHAR(255) CHARACTER SET utf8,
    `cores` INT,
    `threads` INT,
    `frequency` VARCHAR(255) CHARACTER SET utf8,
    `boost_frequency` VARCHAR(255) CHARACTER SET utf8,
    `cache_capacity` VARCHAR(255) CHARACTER SET utf8,
    
    `memory_capacity` VARCHAR(255) CHARACTER SET utf8,
    `memory_type` VARCHAR(255) CHARACTER SET utf8,
    `memory_bus` VARCHAR(255) CHARACTER SET utf8,
    `max_memory_capacity` VARCHAR(255) CHARACTER SET utf8,
    `storage` VARCHAR(255) CHARACTER SET utf8,
    
    `display_size` VARCHAR(255) CHARACTER SET utf8,
    `resolution` VARCHAR(255) CHARACTER SET utf8,
    `refresh_rate` VARCHAR(255) CHARACTER SET utf8,
    `color_gamut` VARCHAR(255) CHARACTER SET utf8,
    `panel_type` VARCHAR(255) CHARACTER SET utf8,
    `touch_screen` VARCHAR(255) CHARACTER SET utf8,
    
    `graphics_card` VARCHAR(255) CHARACTER SET utf8,
    `sound_technology` VARCHAR(255) CHARACTER SET utf8,
    
    `wireless_connectivity` VARCHAR(255) CHARACTER SET utf8,
    `sd_card` VARCHAR(255) CHARACTER SET utf8,
    `webcam` VARCHAR(255) CHARACTER SET utf8,
    `cooling_fan` VARCHAR(255) CHARACTER SET utf8,
    `misc_feature` VARCHAR(255) CHARACTER SET utf8,
    `backlit_keyboard` VARCHAR(255) CHARACTER SET utf8,
    
    `dimension_weight` VARCHAR(255) CHARACTER SET utf8,
    `material` VARCHAR(255) CHARACTER SET utf8,
    
    `battery_capacity` VARCHAR(255) CHARACTER SET utf8,
    `charger_capacity` VARCHAR(255) CHARACTER SET utf8,
    `os` VARCHAR(255) CHARACTER SET utf8,
    `launch_date` INT,
    
    `daily_sold` INT,
    `weekly_sold` INT,
    `monthly_sold` INT,
    `total_sold` INT,
    
    `imported` INT,
    `import_price` FLOAT,
    
    `daily_views` INT,
    `weekly_views` INT,
    `monthly_views` INT,
    `total_views` INT,
    
    `when_created` DATETIME DEFAULT CURRENT_TIMESTAMP,
    `last_updated` DATETIME,
    `hidden` BOOLEAN,
    `deleted` BOOLEAN
);


UPDATE review.products SET when_created = current_timestamp();